import React,{Component} from 'react';

class Login extends Component{
    render (){
        return(
            <div className="login">
                <div className="container">
                    <div className="row">
                        <div className="col-md-8 m-auto">
                            <h1 className="display-4 text-center">Login</h1>
                            <p className="lead text-center">Sign in to your account</p>
                            <form action="dashboar.html">
                                <div className="form-group">
                                    <input type="text" className="form-control form-control-lg" placeholder="Username" name="username" />
                                </div>
                                <div className="form-group">
                                    <input type="password" className="form-control form-control-lg" placeholder="Username" name="password" />
                                </div>
                                <input type="submit" className="btn btn-info btn-block mt-4"  value="submit"/> 
                            </form>

                        </div>
                    </div>
                </div>
            </div>
        );
    }
} 
export default Login;
